import React, { Component, lazy, Suspense } from 'react';
import { Link } from 'react-router-dom';
import {
  Button,
  Badge,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Form,
  FormGroup,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Pagination,
  PaginationItem,
  PaginationLink,
  Row,
  Table,
  FormFeedback
} from 'reactstrap';

import usersData from './Usuarios/UsersData'

function UserRow(props) {
  const user = props.user
  const userLink = `/modificar/${user.id}`

  const getBadge = (status) => {
    return status === 'Active' ? 'success' :
      status === 'Inactive' ? 'secondary' :
        status === 'Pending' ? 'warning' :
          status === 'Banned' ? 'danger' :
            'primary'
  }

  return (
    <tr key={user.id.toString()}>
      <th scope="row"><Link to={userLink}>{user.id}</Link></th>
      <td><Link to={userLink}>{user.name}</Link></td>
      <td>{user.registered}</td>
      <td>{user.role}</td>
      <td><Link to={userLink}><Badge color={getBadge(user.status)}>{user.status}</Badge></Link></td>
    </tr>
  )
}

class ModificarUsuario extends Component {
  constructor(props) {
    super(props);
    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
    this.state = {
      filtercod:'',
      filtercpf:'',
      filteremail:'',
      sqlquery:'',
      radioSelected: 2,
    };

  }

  clearFields = () => {
    this.setState({
      filtercod:'',
      filtercpf:'',
      filteremail:'',
      sqlquery:'',
      response:'',
      post:'',
      responseToPost:''
    });
  }

  performSearch = () => {
    if(!this.state.filtercod && !this.state.filtercpf && !this.state.filteremail){
      alert("Insira um filtro");
    }
    else{
      this.setState({
        sqlquery: "SELECT * FROM USUARIOS WHERE cod_usuario = " + this.state.filtercod + " AND cpf = " + this.state.filtercpf + " AND email =  '" + this.state.filteremail+ "';"
      });

      fetch('api/sql?cod='+this.state.filtercod +'&cpf='+this.state.filtercpf+'&email='+this.state.filteremail)
      .then(res => {res.json})
      .then(res => {
        console.log(res);
      })
    }
  }

  // handleSubmit = async e => {
  //   e.preventDefault();
  //   const response = await fetch('/api/sql', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ post: this.state.filtercpf}),
  //   });
  //   const body = await response.text();

  //   this.setState({ responseToPost: body });
  //   console.log(this.state.responseToPost);
  // };


  handleChange = event => {
    console.log(event.target.id);
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  onRadioBtnClick(radioSelected) {
    this.setState({
      sex: radioSelected.target.value,
    });
  }

  render() {

    const userList = usersData.filter((user) => user.id < 10)

    return (
      <div className="animated fadeIn">
        {/* Filtro de Usuarios */}
        <Row>
          <Col xs="12" md="12">
            <Card>
              <CardHeader>
                <strong>Filtrar Usuários</strong>
              </CardHeader>
              <CardBody>
                <Form action="" method="post" className="form-horizontal">
                  <FormGroup row>
                  <Col md="4">
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-user"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input 
                          type="text" 
                          id="filtercod" 
                          name="filtercod" 
                          placeholder="Código"
                          value={this.state.filtercod}
                          onChange={this.handleChange}
                        />
                      </InputGroup>
                    </Col>
                    <Col md="4">
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-user"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input 
                          type="text" 
                          id="filtercpf" 
                          name="filtercpf" 
                          placeholder="CPF" 
                          value={this.state.filtercpf}
                          onChange={this.handleChange}
                        />
                      </InputGroup>
                    </Col>
                    <Col md="4">
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-envelope-o"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input 
                          type="text" 
                          id="filteremail" 
                          name="filteremail" 
                          placeholder="E-Mail" 
                          value={this.state.filteremail}
                          onChange={this.handleChange}
                        />
                      </InputGroup>
                    </Col>
                  </FormGroup>
                </Form>
              </CardBody>
              <CardFooter>
                <Button type="submit" size="sm" color="success" onClick={this.performSearch} ><i className="fa fa-dot-circle-o"></i> Buscar</Button>
                <Label>&nbsp;</Label>
                <Button type="reset" size="sm" color="danger" onClick={this.clearFields} ><i className="fa fa-ban"></i> Limpar</Button>
              </CardFooter>
            </Card>
          </Col>
          </Row>
        {/* Lista de Usuários */}
          <Row>
          <Col xl={12}>
              <Card>
                <CardHeader>
                  <i className="fa fa-align-justify"></i> Usuários <small className="text-muted"></small>
                </CardHeader>
                <CardBody>
                  <Table responsive hover>
                    <thead>
                      <tr>
                        <th scope="col">Cód.</th>
                        <th scope="col">Nome</th>
                        <th scope="col">Cadastrado em.</th>
                        <th scope="col">Perfil</th>
                        <th scope="col">Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {userList.map((user, index) =>
                        <UserRow key={index} user={user}/>
                      )}
                    </tbody>
                  </Table>
                  <Pagination className="center">
                  <PaginationItem disabled><PaginationLink previous tag="button">Prev</PaginationLink></PaginationItem>
                  <PaginationItem active>
                    <PaginationLink tag="button">1</PaginationLink>
                  </PaginationItem>
                  <PaginationItem><PaginationLink tag="button">2</PaginationLink></PaginationItem>
                  <PaginationItem><PaginationLink tag="button">3</PaginationLink></PaginationItem>
                  <PaginationItem><PaginationLink tag="button">4</PaginationLink></PaginationItem>
                  <PaginationItem><PaginationLink next tag="button">Next</PaginationLink></PaginationItem>
                </Pagination>
                </CardBody>
              </Card>
            </Col>
          </Row>

      </div>
    );
  }
}

export default ModificarUsuario;
