import React, { Component, lazy, Suspense } from 'react';
import { Link } from 'react-router-dom';
import { Bar, Doughnut, Line, Pie, Polar, Radar } from 'react-chartjs-2';
import { CustomTooltips } from '@coreui/coreui-plugin-chartjs-custom-tooltips';
import Popup from "reactjs-popup";

import {
  Button,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  InputGroup,
  InputGroupAddon,
  Label,
  Row,
} from 'reactstrap';


const line = {
  labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
  datasets: [
    {
      label: 'My First dataset',
      fill: false,
      lineTension: 0.1,
      backgroundColor: 'rgba(75,192,192,0.4)',
      borderColor: 'rgba(75,192,192,1)',
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: 'rgba(75,192,192,1)',
      pointBackgroundColor: '#fff',
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: 'rgba(75,192,192,1)',
      pointHoverBorderColor: 'rgba(220,220,220,1)',
      pointHoverBorderWidth: 2,
      pointRadius: 1,
      pointHitRadius: 10,
      data: [65, 59, 80, 81, 56, 55, 40],
    },
  ],
};

const bar = {
  labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
  datasets: [
    {
      label: 'My First dataset',
      backgroundColor: 'rgba(255,99,132,0.2)',
      borderColor: 'rgba(255,99,132,1)',
      borderWidth: 1,
      hoverBackgroundColor: 'rgba(255,99,132,0.4)',
      hoverBorderColor: 'rgba(255,99,132,1)',
      data: [65, 59, 80, 81, 56, 55, 40],
    },
  ],
};

const doughnut = {
  labels: [
    'Red',
    'Green',
    'Yellow',
  ],
  datasets: [
    {
      data: [300, 50, 100],
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
      ],
      hoverBackgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
      ],
    }],
};

const radar = {
  labels: ['Eating', 'Drinking', 'Sleeping', 'Designing', 'Coding', 'Cycling', 'Running'],
  datasets: [
    {
      label: 'My First dataset',
      backgroundColor: 'rgba(179,181,198,0.2)',
      borderColor: 'rgba(179,181,198,1)',
      pointBackgroundColor: 'rgba(179,181,198,1)',
      pointBorderColor: '#fff',
      pointHoverBackgroundColor: '#fff',
      pointHoverBorderColor: 'rgba(179,181,198,1)',
      data: [65, 59, 90, 81, 56, 55, 40],
    },
    {
      label: 'My Second dataset',
      backgroundColor: 'rgba(255,99,132,0.2)',
      borderColor: 'rgba(255,99,132,1)',
      pointBackgroundColor: 'rgba(255,99,132,1)',
      pointBorderColor: '#fff',
      pointHoverBackgroundColor: '#fff',
      pointHoverBorderColor: 'rgba(255,99,132,1)',
      data: [28, 48, 40, 19, 96, 27, 100],
    },
  ],
};

const pie = {
  labels: [
    'Red',
    'Green',
    'Yellow',
  ],
  datasets: [
    {
      data: [300, 50, 100],
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
      ],
      hoverBackgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
      ],
    }],
};

const polar = {
  datasets: [
    {
      data: [
        11,
        16,
        7,
        3,
        14,
      ],
      backgroundColor: [
        '#FF6384',
        '#4BC0C0',
        '#FFCE56',
        '#E7E9ED',
        '#36A2EB',
      ],
      label: 'My dataset' // for legend
    }],
  labels: [
    'Red',
    'Green',
    'Yellow',
    'Grey',
    'Blue',
  ],
};

const options = {
  tooltips: {
    enabled: false,
    custom: CustomTooltips
  },
  maintainAspectRatio: false
}

class PersonalityInsights extends Component {
  constructor(props) {
    super(props);
    this.validateForm = this.validateForm.bind(this);
    this.openPopup = this.openPopup.bind(this);
    this.closePopup = this.closePopup.bind(this);
    this.loadUser = this.loadUser.bind(this);

    this.state = {
      nome:'',
      sobrenome:'',
      data_nascimento:'',
      cpf:'',
      email:'',
      telefone:'',
      celular:'',
      cep:'',
      endereco_numero:'',
      logradouro:'', 
      bairro: '',
      localidade:'',
      uf: '',
      complemento: '',
      cod_localidade: '',
      popup: {
        open : false,
        message : ''
      },
      formMessage: {
        message : '',
        type : ''
      },
      invalid : {
        nome: false,
        sobrenome: false,
        data_nascimento: false,
        cpf: false,
        email: false,
        telefone: false,
        celular: false,
        cep: false,
        endereco_numero: false,
        logradouro: false, 
        bairro: false,
        localidade: false,
        uf: false,
        complemento: false,
        cod_localidade: false,
      },
      valid : {
        nome: false,
        sobrenome: false,
        data_nascimento: false,
        cpf: false,
        email: false,
        telefone: false,
        celular: false,
        cep: false,
        endereco_numero: false,
      },
    };
  }

  componentDidMount(){
    this.loadUser();
  }

  loadUser(){
    fetch('api/selectCandidatoModify?table=CANDIDATO&cod='+this.props.match.params.id)
    .then(res => res.json())
    .then(res => {
      console.log(res.recordset)
      this.setState({
        nome: res.recordset[0].nome,
        sobrenome: res.recordset[0].sobrenome,
        data_nascimento:res.recordset[0].data_nascimento,
        cpf:res.recordset[0].cpf,
        email:res.recordset[0].email,
        telefone:res.recordset[0].telefone,
        celular:res.recordset[0].celular,
        cep:res.recordset[0].cep,
        endereco_numero:res.recordset[0].endereco_numero,
        cod_localidade: res.recordset[0].cod_localidade,
      });

      fetch('api/selectCodLocalidade?table=LOCALIDADE&cod='+this.state.cod_localidade)
      .then(res => res.json())
      .then(res => {
        console.log("Localidade : "+res);
        this.setState({
          cep: res.recordset[0].cep,
          logradouro:res.recordset[0].logradouro, 
          bairro: res.recordset[0].bairro,
          localidade:res.recordset[0].localidade,
          uf: res.recordset[0].uf,
          complemento: res.recordset[0].complemento,
        });
      })
      .catch(err => {
        this.openPopup("Erro ao carregar localidade : " +err);
      });
    })
    .catch(err => {
      this.openPopup("Erro ao carregar candidato : " +err);
    });
  }

  validateForm(id, value, isvalid){
    var invalid = {...this.state.invalid}
    var valid = {...this.state.valid}
    const invalidkeys = Object.keys(invalid);
    invalidkeys.forEach(e => {
      if(e == id){
        if(id == "cep"){
          if(isvalid){
            invalid[e] = false;
            valid[e] = true
            this.setState({
              formMessage : {
                message : '',
                type : ''
              }
            });
          }
          else{
            invalid[e] = true;
            valid[e] = false;
            this.setState({
              formMessage : {
                message : "Insira um CEP vÃ¡lido",
                type : 'error'
              },
              logradouro : '',
              bairro:'',
              localidade:'',
              uf:'', 
            });
          }
        }
        else{
          if(value){
            invalid[e] = false;
            valid[e] = true;
          }
          else{
            invalid[e] = true;
            valid[e] = false;
          }
        }
      }
    });
    this.setState({
      invalid : invalid,
      valid : valid
    });    
  }

  renderMessage(){
    return(
      <Label>{this.state.formMessage.message}</Label>
    )
  }

  validateCEP = () => {
    if(this.state.cep.length < 8){
      this.validateForm("cep", this.state.cep, false);
    }else{
