import React, { Component, lazy, Suspense } from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';
import Popup from "reactjs-popup";
import axios from 'axios';
import {
  Button,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  InputGroup,
  InputGroupAddon,
  Label,
  Row,
} from 'reactstrap';
class RegisterUser extends Component {
  constructor(props) {
    super(props);
    this.validateForm = this.validateForm.bind(this);
    this.openPopup = this.openPopup.bind(this);
    this.closePopup = this.closePopup.bind(this);
    this.state = {
      nome:'',
      sobrenome:'',
      data_nascimento:'',
      cpf:'',
      email:'',
      telefone:'',
      celular:'',
      cep:'',
      endereco_numero:'',
      logradouro:'', 
      bairro: '',
      localidade:'',
      uf: '',
      complemento: '',
      cod_localidade: '',
      selectedFile: null,
      cod_candidato: '',
      popup: {
        open : false,
        message : ''
      },
      formMessage: {
        message : '',
        type : ''
      },
      invalid : {
        nome: false,
        sobrenome: false,
        data_nascimento: false,
        cpf: false,
        email: false,
        telefone: false,
        celular: false,
        cep: false,
        endereco_numero: false,
        logradouro: false, 
        bairro: false,
        localidade: false,
        uf: false,
        complemento: false,
        cod_localidade: false,
      },
      valid : {
        nome: false,
        sobrenome: false,
        data_nascimento: false,
        cpf: false,
        email: false,
        telefone: false,
        celular: false,
        cep: false,
        endereco_numero: false,
      },
    };


  }

  validateForm(id, value, isvalid){
    var invalid = {...this.state.invalid}
    var valid = {...this.state.valid}
    const invalidkeys = Object.keys(invalid);
    invalidkeys.forEach(e => {
      if(e == id){
        if(id == "cep"){
          if(isvalid){
            invalid[e] = false;
            valid[e] = true
            this.setState({
              formMessage : {
                message : '',
                type : ''
              }
            });
          }
          else{
            invalid[e] = true;
            valid[e] = false;
            this.setState({
              logradouro : '',
              bairro:'',
              localidade:'',
              uf:'', 
            });
          }
        }
        else if(id == "email"){
          if(value.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
            invalid[e] = false;
            valid[e] = true;
            this.setState({
              formMessage : {
                message : '',
                type : ''
              }
            });
          }
          else{
            invalid[e] = true;
            valid[e] = false;
           }
        }
        else{
          if(value){
            invalid[e] = false;
            valid[e] = true;
          }
          else{
            invalid[e] = true;
            valid[e] = false;
          }
        }
      }
    });
    this.setState({
      invalid : invalid,
      valid : valid
    });    
  }

// sendfFile = () => {
//   const data = new FormData() 
//   data.append('file', this.state.selectedFile);
//   console.log(this.state.selectedFile);
//   //data.append('cod_candidato', this.state.cod_candidato);
//   axios.post("api/personality/upload", data, { 
//   })
//   .then(res => {
//       console.log(res.statusText)
//       //this.openPopup("Usuário Cadastrado com Sucesso" + res);
//       //this.clearFields();
//   })
//   .catch(err => {
//     console.log("Erro");
//       console.log(err);
//   });
// }

registerUser = () => {

  var valid = {...this.state.valid}
  const validvalues = Object.values(valid);
  var invalid = false;
  for (let i = 0; i < validvalues.length; i++) {
      if(!validvalues[i]){
          console.log("Invalido" + i);
          invalid = true;
          this.validateForm();
          break;
      }      
  }
  if(!invalid && this.state.selectedFile){
    axios.post("api/register/newcandidate", {
      "table" : "CANDIDATO",
      "values": {
          "nome" : this.state.nome,
          "sobrenome" : this.state.sobrenome,
          "data_nascimento" : this.state.data_nascimento,
          "cpf" : this.state.cpf,
          "email" : this.state.email,
          "telefone" : this.state.telefone,
          "celular" : this.state.celular,
          "endereco_numero" : this.state.endereco_numero,
          "cod_localidade" : this.state.cod_localidade
      }
  })
  .then(res => {
      fetch('api/search/candidate?table=CANDIDATO&cpf='+this.state.cpf)
      .then(res => res.json())
      .then(res => {
          this.setState({
              cod_candidato:res.recordset[0].cod_candidato,
          });
          const data = new FormData() 
          data.append('file', this.state.selectedFile);
          console.log(this.state.selectedFile);
          data.append('cod_candidato', this.state.cod_candidato);
          axios.post("api/personality/upload", data, { 
          })
          .then(res => {
              console.log(res.statusText)
              this.openPopup("Usuário Cadastrado com Sucesso" + res);
              this.clearFields();
          })
          .catch(err => {
              console.log(err);
          });
      })

      .catch(err => {
          this.openPopup("Erro ao recuperar código do candidato : " + err);
      });

  })
  .catch(err => {
      console.log(err)
  }); 
  }
}

  validateCEP = () => {
    if(this.state.cep.length < 8){
      this.validateForm("cep", this.state.cep, false);
    }else{
      fetch('api/register/getzipcode?table=LOCALIDADE&cep='+this.state.cep)
      .then(res => res.json())
      .then(res => {
        this.setState({
          cod_localidade: res.recordset[0].cod_localidade,
          bairro : res.recordset[0].bairro,
          localidade : res.recordset[0].localidade,
          logradouro : res.recordset[0].logradouro,
          uf : res.recordset[0].uf
        });
        this.validateForm("cep", this.state.cep, true);
      })
      .catch(err => {
        this.openPopup("Erro ao validar CEP :" + err);
    });
    }
  }

  clearFields = () => {

    var invalid = {...this.state.invalid}
    var valid = {...this.state.valid}
    const invalidkeys = Object.keys(invalid);
    invalidkeys.forEach(e => {
      invalid[e] = null
    });
    const validkeys = Object.keys(valid);
    validkeys.forEach(e => {
      valid[e] = null
    });
    this.setState({
      nome:'',
      sobrenome:'',
      data_nascimento:'',
      cpf:'',
      email:'',
      telefone:'',
      celular:'',
      cep:'',
      endereco_numero:'',
      logradouro:'', 
      bairro: '',
      localidade:'',
      uf: '',
      complemento: '',
      cod_localidade: '',
      invalid : invalid,
      valid : valid,
      selectedFile : null
    });
  }

  handleChange = event => {
    var value = '';
    if(event.target.id == "cep"){
      value = event.target.value.replace(/([^\d])/, '');

    } 
    else{
      value = event.target.value
    }      
    this.setState({
      [event.target.id]: value
    });
    this.validateCEP();
    this.validateForm(event.target.id, value);
  }

  handleChangeFile = event => {
    this.setState({
      selectedFile: event.target.files[0],
      loaded: 0,
    });

  }

  openPopup(message){
    this.setState({
      popup : {
        open : true,
        message : message
      }
    });
  }

  closePopup(){
    this.setState({
      popup: {
        open : false,
        message: ''
      }
    });
  }

  render() {
    return (
      <div className="animated fadeIn">
        <Card>
        <CardHeader>
          <strong>Novo Candidato</strong>
        </CardHeader>
        <CardBody>
        <Row>

          <Col xs="12" md="6">
            <Card>
              <CardHeader>
                <strong><i className="cui-user-follow"></i>&nbsp;Informações Pessoais</strong>
              </CardHeader>
              <CardBody>
                <Form action="" method="post" encType="multipart/form-data" className="form-horizontal">
                  <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="nome">Nome</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input 
                          type="text" 
                          id="nome" 
                          name="nome" 
                          onChange={this.handleChange} 
                          value={this.state.nome} 
                          invalid={this.state.invalid.nome} 
                          valid={this.state.valid.nome} 
                          required
                          />
                          <FormFeedback invalid="">Insira o nome do candidato</FormFeedback>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Sobrenome</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input 
                          type="text" 
                          id="sobrenome" 
                          name="sobrenome" 
