import React, { Component, lazy, Suspense } from 'react';
import $ from 'jquery';
import InputMask from 'react-input-mask';
import Popup from "reactjs-popup";

import {
  Button,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Form,
  FormGroup,
  FormFeedback,
  Input,
  InputGroup,
  InputGroupAddon,
  Label,
  Row,
} from 'reactstrap';


class CadastrarUsuario extends Component {
  constructor(props) {
    super(props);
    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
    this.state = {
      nome: {
        value: '',
      },
      sobrenome: {
        value:'',
        valid: false
      },
      data_nascimento: {
        value:'',
        valid: false
      },
      cpf: {
        value:'',
        valid: false
      },
      email:  {
        value:'',
        valid: false
      },
      telefone:  {
        value:'',
        valid: false
      },
      celular:  {
        value:'',
        valid: false
      },
      sex: {
        value:'',
        valid: false
      },
      cep: {
        value:'',
        valid: false
      },
      endereco_numero: {
        value:'',
        valid: false
      },
      logradouro: {
        value:'',
        valid: false
      },
      bairro: {
        value:'',
        valid: false
      },
      localidade: {
        value:'',
        valid: false
      },
      uf: {
        value:'',
        valid: false
      },
      complemento: {
        value:'',
        valid: false
      },
      cod_localidade: {
        value:'',
        valid: false
      },
      popup: {
        open : false,
        message : ''
      },
      formMessage: {
        message : '',
        type : ''
      },
      radioSelected: 2
    };

    this.openPopup = this.openPopup.bind(this);
    this.closePopup = this.closePopup.bind(this);
  }

  checkBeforeRegister = () => {
    this.setState({nome : {valid : true}});
  }

  registerUser = () => {
    fetch('api/insertCandidato?&nome='+this.state.nome.value
    +'&sobrenome='+this.state.sobrenome.value
    +'&data_nascimento='+this.state.data_nascimento.value
    +'&cpf='+this.state.cpf.value
    +'&email='+this.state.email.value
    +'&telefone='+this.state.telefone.value
    +'&celular='+this.state.celular.value
    +'&endereco_numero='+this.state.endereco_numero.value
    +'&cod_localidade='+this.state.cod_localidade.value)
    .then(res => res.json())
    .then(res => {
        this.popupMessage("Usuário Cadastrado com Sucesso!" + res);
        this.openPopup();
      })
    .catch(err => {
      console.log("Erro" +err);
    });
  }


  renderMessage(){
    return(
      <Label>{this.state.formMessage.message}</Label>
    )

  }

  validateCEP = () => {
    if(this.state.cep.value.length < 8){
      this.setState({
        formMessage : {
          message : "Insira um CEP válido",
          type : 'error'
        }
      });
    }else{
      fetch('api/selectcep?table=LOCALIDADE&cep='+this.state.cep)
      .then(res => res.json())
      .then(res => {
        this.setState({
          cod_localidade: res.recordset[0].cod_localidade,
          bairro : res.recordset[0].bairro,
          localidade : res.recordset[0].localidade,
          logradouro : res.recordset[0].logradouro,
          uf : res.recordset[0].uf
        })
        })
      .catch(err => {
        console.log("Erro" +err);
      });
    }
  }

  clearFields = () => {
    this.setState({
      fullname: '',
      email:'',
      cpf:'',
      data_nascimento:'',
      sex: '',
      cep: '',
      endereco_numero: '',
      logradouro: '',
      bairro:'',
      cidade:'',
      uf:'',
      complemento:''
    });
  }


  handleChange = event => {
    var value = '';
    if(event.target.id == "cep")      value = event.target.value.replace(/([^\d])/, '');
    else      value = event.target.value
    if(!event.target.value){
      console.log(event.target.value);
    }

    this.setState({
      [event.target.id]: {value : value}
    });
  }

  onRadioBtnClick(radioSelected) {
    this.setState({
      sex: radioSelected.target.value,
    });
  }


  openPopup(message){
    this.setState({
      popup : {
        open : true,
        message : message
      }
    });
  }

  closePopup(){
    this.setState({
      popup: {
        open : false,
        message: ''
      }
    });
  }

  render() {
    return (
      <div className="animated fadeIn">
        <Card>
        <CardHeader>
          <strong>Novo Candidato</strong>
        </CardHeader>
        <CardBody>
        <Row>
          <Col xs="12" md="6">
            <Card>
              <CardHeader>
                <strong><i className="cui-user-follow"></i>&nbsp;Informações Pessoais</strong>
              </CardHeader>
              <CardBody>
                <Form action="" method="post" encType="multipart/form-data" className="form-horizontal">
                  <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Nome</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="nome" name="nome" onChange={this.handleChange} value={this.state.nome.value} valid={this.state.nome.valid} />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Sobrenome</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="sobrenome" name="sobrenome" onChange={this.handleChange} value={this.state.sobrenome.value} valid={this.state.sobrenome.valid} />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="email-input">Email</Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input type="email" id="email" name="email" autoComplete="email" onChange={this.handleChange} value={this.state.email.value} valid={this.state.email.valid}/>
                    </Col>
                     </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">CPF</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="cpf" name="cpf" onChange={this.handleChange} value={this.state.cpf.value} valid={this.state.cpf.valid}/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="date-input">Data de Nascimento</Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input type="date" id="data_nascimento" name="data_nascimento" placeholder="date" onChange={this.handleChange} value={this.state.data_nascimento.value} valid={this.state.data_nascimento.valid} />
                    </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Telefone</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="telefone" name="telefone" onChange={this.handleChange} value={this.state.telefone.value} valid={this.state.telefone.valid}/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Celular</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="celular" name="celular" onChange={this.handleChange} value={this.state.celular.value} valid={this.state.celular.valid} />
                      </Col>
                    </FormGroup>
                  <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="file-input">Curriculo</Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input type="file" id="file-input" name="file-input" />
                    </Col>
                  </FormGroup>
                </Form>
              </CardBody>
            </Card>
          </Col>
          <Col xs="12" md="6">
            <Card>
              <CardHeader>
                <strong><i className="cui-location-pin"></i>&nbsp;Endereço</strong>
              </CardHeader>
              <CardBody>
                <Form action="" method="post" encType="multipart/form-data" className="form-horizontal">
                  <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">CEP</Label>
                      </Col>
                      <Col xs="12" md="4">
                      <InputGroup>
                        <Input type="text" id="cep" name="cep" placeholder="CEP" onChange={this.handleChange} value={this.state.cep.values} maxLength="8" valid={this.state.cep.valid}/>
                        <InputGroupAddon addonType="append">
                          <Button type="button" color="success" onClick={this.validateCEP}>Validar</Button>
                        </InputGroupAddon>
                      </InputGroup>
                        {/* <Input maxLength="8" type="text" id="cep" name="text-input" onChange={this.handleChangeCEP} value={this.state.cep}/> */}
                        {/* <InputMask mask="99999-999" type="text" id="cep" name="text-input" onChange={this.handleChangeCEP} value={this.state.cep}/> */}
                      </Col>
                      <Col md="2">
                        <Label htmlFor="text-input">Número</Label>
                      </Col>
                      <Col xs="12" md="4">
                        <Input type="text" id="endereco_numero" name="endereco_numero" onChange={this.handleChange} value={this.state.endereco_numero.value}/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Col md="2">
                      <Label htmlFor="text-input">Complemento</Label>
                    </Col>
                    <Col xs="12" md="10">
                      <Input type="text" id="complemento" name="complemento" value={this.state.complemento} onChange={this.handleChange}/>
                    </Col>
                     </FormGroup>
                    <FormGroup row>
                    <Col md="2">
                      <Label htmlFor="text-input">Logradouro</Label>
                    </Col>
                    <Col xs="12" md="10">
                      <Input type="text" id="logradouro" name="logradouro" value={this.state.logradouro} readOnly/>
                    </Col>
                     </FormGroup>
                    <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">Bairro</Label>
                      </Col>
                      <Col xs="12" md="10">
                        <Input type="text" id="bairro" name="bairro" value={this.state.bairro} readOnly/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">Cidade</Label>
                      </Col>
                      <Col xs="12" md="10">
                        <Input type="text" id="localidade" name="localidade" value={this.state.localidade} readOnly/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">UF</Label>
                      </Col>
                      <Col xs="12" md="10">
                        <Input type="text" id="uf" name="text-input" value={this.state.uf} readOnly/>
                      </Col>
                    </FormGroup>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
        </CardBody>
        <CardFooter>
               <Popup open={this.state.popup.open} closeOnDocumentClick onClose={this.closePopup}>{this.state.popup.message}</Popup>
               <Button type="submit" size="sm" color="success" onClick={this.checkBeforeRegister}><i className="fa fa-dot-circle-o"></i> Enviar</Button>
                &nbsp;
                <Button type="reset" id="reset" size="sm" color="danger" onClick={this.clearFields}><i className="fa fa-ban" ></i> Limpar</Button>
                &nbsp;
                {this.renderMessage()}
        </CardFooter> 
        </Card>
      </div>
    );
  }
}
export default CadastrarUsuario;
