import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Popup from "reactjs-popup";
import {
  Button,
  Badge,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Form,
  FormGroup,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Pagination,
  PaginationItem,
  PaginationLink,
  Row,
  Table,
} from 'reactstrap';

class ModificarUsuario extends Component {
  constructor(props) {
    super(props);
    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
    this.renderMessage = this.renderMessage.bind(this);
    this.openPopup = this.openPopup.bind(this);
    this.closePopup = this.closePopup.bind(this);
    this.state = {
      filtercod:'%',
      filtercpf:'',
      filteremail:'',
      usersData: [],
      popup: {
        open : false,
        message : ''
      },
    };
  }

  componentDidMount(){
    this.performSearch();
    this.setState({
      filtercod: ''
    });
  }

  clearFields = () => {
    this.setState({
      filtercod:'',
      filtercpf:'',
      filteremail:'',
      sqlquery:'',
      response:'',
      post:'',
      responseToPost:''
    });
  }

  performSearch = () => {
      fetch('api/selectCandidato?table=CANDIDATO&cod='+this.state.filtercod +'&cpf='+this.state.filtercpf+'&email='+this.state.filteremail)
      .then(res => res.json())
      .then(res => {
        var aux = [];
        res.recordset.forEach(e => {
          aux.push(e);
        });
        this.setState({
          usersData : aux
        })
        this.renderTableData();

      })
      .catch(err => {
        this.openPopup("Erro ao filtrar candidatos. " + err);
      });
  }

  renderTableData(){
    return this.state.usersData.map((usersData, index) => {
      const {cod_candidato, nome, sobrenome, endereco } = usersData;
      const userLink = `/modificar/${cod_candidato}`
      return (
        <tr key={cod_candidato}>
          <th scope="row"><Link to={userLink}>{cod_candidato}</Link></th>
          <td><Link to={userLink}>{nome}</Link></td>
          <td></td>
          <td></td>
          <td><Link to={userLink}></Link></td>
        </tr>
      )
    });
    }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  onRadioBtnClick(radioSelected) {
    this.setState({
      sex: radioSelected.target.value,
    }); 
  }

  openPopup(message){
    this.setState({
      popup : {
        open : true,
        message : message
      }
    });
  }

  closePopup(){
    this.setState({
      popup: {
        open : false,
        message: ''
      }
    });
  }


  render() {
    return (
      <div className="animated fadeIn">
        {/* Filtro de Usuarios */}
        <Row>
          <Col xs="12" md="12">
            <Card>
              <CardHeader>
                <strong>Filtrar Usuários</strong>
              </CardHeader>
              <CardBody>
                <Form action="" method="post" className="form-horizontal">
                  <FormGroup row>
                  <Col md="4">
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-user"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input 
                          type="text" 
                          id="filtercod" 
                          name="filtercod" 
                          placeholder="Código"
                          value={this.state.filtercod}
                          onChange={this.handleChange}
                        />
                      </InputGroup>
                    </Col>
                    <Col md="4">
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-user"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input 
                          type="text" 
                          id="filtercpf" 
                          name="filtercpf" 
                          placeholder="CPF" 
                          value={this.state.filtercpf}
                          onChange={this.handleChange}
                        />
                      </InputGroup>
                    </Col>
                    <Col md="4">
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-envelope-o"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input 
                          type="text" 
                          id="filteremail" 
                          name="filteremail" 
                          placeholder="E-Mail" 
                          value={this.state.filteremail}
                          onChange={this.handleChange}
                        />
                      </InputGroup>
                    </Col>
                  </FormGroup>
                </Form>
              </CardBody>
              <CardFooter>
                <Button type="submit" size="sm" color="success" onClick={this.performSearch}><i className="fa fa-dot-circle-o"></i> Buscar</Button>
                <Label>&nbsp;</Label>
                <Button type="reset" size="sm" color="danger" onClick={this.clearFields}><i className="fa fa-ban"></i> Limpar</Button>
              </CardFooter>
            </Card>
          </Col>
          </Row>
          <Popup open={this.state.popup.open} closeOnDocumentClick onClose={this.closePopup}>
               <Card className="text-white bg-primary py-5 d-md-down-none" style={{ width: '100%' }}>
                  <CardBody className="text-center">
                    <div>
                      <h2>{this.state.popup.message}</h2>
                      {/* <Link to="/dashboard">
                        <Button color="primary" className="mt-3" active tabIndex={-1}>Menu</Button>
                      </Link> */}
                    </div>
                  </CardBody>
                </Card>
               </Popup>
        {/* Lista de Usuários */}
          <Row>
          <Col xl={12}>
              <Card>
                <CardHeader>
