import React, { Component, lazy, Suspense } from 'react';
import $ from 'jquery';
import InputMask from 'react-input-mask';
import Popup from "reactjs-popup";

import {
  Button,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Form,
  FormGroup,
  FormFeedback,
  Input,
  InputGroup,
  InputGroupAddon,
  Label,
  Row,
} from 'reactstrap';


class CadastrarUsuario extends Component {
  constructor(props) {
    super(props);
    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
    this.state = {
      nome:'',
      sobrenome:'',
      data_nascimento:'',
      cpf:'',
      email:'',
      telefone:'',
      celular:'',
      sex:'',
      cep:'',
      endereco_numero:'',
      logradouro:'', 
      bairro: '',
      localidade:'',
      uf: '',
      complemento: '',
      cod_localidade: '',
      popup: {
        open : false,
        message : ''
      },
      formMessage: {
        message : '',
        type : ''
      },
      invalid : {
        nome: false,
        sobrenome: false,
        data_nascimento: false,
        cpf: false,
        email: false,
        telefone: false,
        celular: false,
        sex: false,
        cep: false,
        endereco_numero: false,
        logradouro: false, 
        bairro: false,
        localidade: false,
        uf: false,
        complemento: false,
        cod_localidade: false,
      },
      radioSelected: 2,
    };

    this.openPopup = this.openPopup.bind(this);
    this.closePopup = this.closePopup.bind(this);
  }

  checkBeforeRegister = () => {

    if(!this.state.nome){
      this.setState({invalid : {nome : true}});
    }
    if(!this.state.sobrenome){
      this.setState({invalid : {sobrenome : false}});
    } 
    // else  this.setState({invalid : {sobrenome : true}});
    if(!this.state.email){
      this.setState({invalid : {email : false}});
    } 
    // else  this.setState({invalid : {email : true}});
    if(!this.state.cpf){
      this.setState({invalid : {cpf : false}});
    } 
    // else  this.setState({invalid : {cpf : true}});

    if(this.state.data_nascimento  ==''){
      this.setState({invalid : {data_nascimento : false}});
    } 
    // else  this.setState({invalid : {data_nascimento : true}});

    if(this.state.telefone  ==''){
      this.setState({invalid : {telefone : false}});
    } 
    // else  this.setState({invalid : {telefone : true}});

    if(this.state.celular  ==''){
      this.setState({celular : {nome : false}});
    } 
    // else  this.setState({invalid : {nome : true}});

    if(this.state.cep  ==''){
      this.setState({invalid : {cep : false}});
    }
    // else  this.setState({invalid : {cep : true}});
    if(this.state.endereco_numero  ==''){
      this.setState({invalid : {endereco_numero : false}});
    } 
    // else  this.setState({invalid : {endereco_numero : true}});
  }

  registerUser = () => {
    fetch('api/insertCandidato?&nome='+this.state.nome
    +'&sobrenome='+this.state.sobrenome
    +'&data_nascimento='+this.state.data_nascimento
    +'&cpf='+this.state.cpf
    +'&email='+this.state.email
    +'&telefone='+this.state.telefone
    +'&celular='+this.state.celular
    +'&endereco_numero='+this.state.endereco_numero
    +'&cod_localidade='+this.state.cod_localidade)
    .then(res => res.json())
    .then(res => {
        this.popupMessage("Usuário Cadastrado com Sucesso!" + res);
        this.openPopup();
      })
    .catch(err => {
      console.log("Erro" +err);
    });
  }


  renderMessage(){
    return(
      <Label>{this.state.formMessage.message}</Label>
    )

  }

  validateCEP = () => {
    if(this.state.cep.length < 8){
      this.setState({
        formMessage : {
          message : "Insira um CEP válido",
          type : 'error'
        }
      });
    }else{
      fetch('api/selectcep?table=LOCALIDADE&cep='+this.state.cep)
      .then(res => res.json())
      .then(res => {
        this.setState({
          user : {
            cod_localidade: res.recordset[0].cod_localidade,
            bairro : res.recordset[0].bairro,
            localidade : res.recordset[0].localidade,
            logradouro : res.recordset[0].logradouro,
            uf : res.recordset[0].uf
          }
        });
      })
      .catch(err => {
        console.log("Erro" +err);
      });
    }
  }

  clearFields = () => {
    this.setState({
      fullname: '',
      email:'',
      cpf:'',
      data_nascimento:'',
      sex: '',
      cep: '',
      endereco_numero: '',
      logradouro: '',
      bairro:'',
      cidade:'',
      uf:'',
      complemento:''
    });
  }


  handleChange = event => {
    var value = '';
    if(event.target.id == "cep")      value = event.target.value.replace(/([^\d])/, '');
    else      value = event.target.value
    this.setState({
      [event.target.id]: value
    });
  }

  onRadioBtnClick(radioSelected) {
    this.setState({
      sex: radioSelected.target.value,
    });
  }


  openPopup(message){
    this.setState({
      popup : {
        open : true,
        message : message
      }
    });
  }

  closePopup(){
    this.setState({
      popup: {
        open : false,
        message: ''
      }
    });
  }

  render() {
    return (
      <div className="animated fadeIn">
        <Card>
        <CardHeader>
          <strong>Novo Candidato</strong>
        </CardHeader>
        <CardBody>
        <Row>
          <Col xs="12" md="6">
            <Card>
              <CardHeader>
                <strong><i className="cui-user-follow"></i>&nbsp;Informações Pessoais</strong>
              </CardHeader>
              <CardBody>
                <Form action="" method="post" encType="multipart/form-data" className="form-horizontal">
                  <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Nome</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="nome" name="nome" onChange={this.handleChange} value={this.state.nome} invalid={this.state.invalid.nome} />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Sobrenome</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="sobrenome" name="sobrenome" onChange={this.handleChange} value={this.state.sobrenome} invalid={this.state.invalid.sobrenome} />
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="email-input">Email</Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input type="email" id="email" name="email" autoComplete="email" onChange={this.handleChange} value={this.state.email} invalid={this.state.invalid.email}/>
                    </Col>
                     </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">CPF</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="cpf" name="cpf" onChange={this.handleChange} value={this.state.cpf} invalid={this.state.invalid.cpf}/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="date-input">Data de Nascimento</Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input type="date" id="data_nascimento" name="data_nascimento" placeholder="date" onChange={this.handleChange} value={this.state.data_nascimento} invalid={this.state.invalid.data_nascimento} />
                    </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Telefone</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="telefone" name="telefone" onChange={this.handleChange} value={this.state.telefone} invalid={this.state.invalid.telefone}/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="3">
                        <Label htmlFor="text-input">Celular</Label>
                      </Col>
                      <Col xs="12" md="9">
                        <Input type="text" id="celular" name="celular" onChange={this.handleChange} value={this.state.celular} invalid={this.state.invalid.celular} />
                      </Col>
                    </FormGroup>
                  <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="file-input">Curriculo</Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input type="file" id="file-input" name="file-input" />
                    </Col>
                  </FormGroup>
                </Form>
              </CardBody>
            </Card>
          </Col>
          <Col xs="12" md="6">
            <Card>
              <CardHeader>
                <strong><i className="cui-location-pin"></i>&nbsp;Endereço</strong>
              </CardHeader>
              <CardBody>
                <Form action="" method="post" encType="multipart/form-data" className="form-horizontal">
                  <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">CEP</Label>
                      </Col>
                      <Col xs="12" md="4">
                      <InputGroup>
                        <Input type="text" id="cep" name="cep" placeholder="CEP" onChange={this.handleChange} value={this.state.cep} maxLength="8" invalid={this.state.invalid.cep}/>
                        <InputGroupAddon addonType="append">
                          <Button type="button" color="success" onClick={this.validateCEP}>Validar</Button>
                        </InputGroupAddon>
                      </InputGroup>
                        {/* <Input maxLength="8" type="text" id="cep" name="text-input" onChange={this.handleChangeCEP} value={this.state.cep}/> */}
                        {/* <InputMask mask="99999-999" type="text" id="cep" name="text-input" onChange={this.handleChangeCEP} value={this.state.cep}/> */}
                      </Col>
                      <Col md="2">
                        <Label htmlFor="text-input">Número</Label>
                      </Col>
                      <Col xs="12" md="4">
                        <Input type="text" id="endereco_numero" name="endereco_numero" onChange={this.handleChange} value={this.state.endereco_numero}/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Col md="2">
                      <Label htmlFor="text-input">Complemento</Label>
                    </Col>
                    <Col xs="12" md="10">
                      <Input type="text" id="complemento" name="complemento" value={this.state.complemento} onChange={this.handleChange}/>
                    </Col>
                     </FormGroup>
                    <FormGroup row>
                    <Col md="2">
                      <Label htmlFor="text-input">Logradouro</Label>
                    </Col>
                    <Col xs="12" md="10">
                      <Input type="text" id="logradouro" name="logradouro" value={this.state.logradouro} readOnly/>
                    </Col>
                     </FormGroup>
                    <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">Bairro</Label>
                      </Col>
                      <Col xs="12" md="10">
                        <Input type="text" id="bairro" name="bairro" value={this.state.bairro} readOnly/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">Cidade</Label>
                      </Col>
                      <Col xs="12" md="10">
                        <Input type="text" id="localidade" name="localidade" value={this.state.localidade} readOnly/>
                      </Col>
                    </FormGroup>
                    <FormGroup row>
                      <Col md="2">
                        <Label htmlFor="text-input">UF</Label>
                      </Col>
                      <Col xs="12" md="10">
                        <Input type="text" id="uf" name="text-input" value={this.state.uf} readOnly/>
                      </Col>
                    </FormGroup>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
        </CardBody>
        <CardFooter>
               <Popup open={this.state.popup.open} closeOnDocumentClick onClose={this.closePopup}>{this.state.popup.message}</Popup>
               <Button type="submit" size="sm" color="success" onClick={this.checkBeforeRegister}><i className="fa fa-dot-circle-o"></i> Enviar</Button>
                &nbsp;
                <Button type="reset" id="reset" size="sm" color="danger" onClick={this.clearFields}><i className="fa fa-ban" ></i> Limpar</Button>
                &nbsp;
                {this.renderMessage()}
        </CardFooter> 
        </Card>
      </div>
    );
  }
}
export default CadastrarUsuario;
