import React, { Component, lazy, Suspense } from 'react';
import { Bar, Line } from 'react-chartjs-2';
import { Link } from 'react-router-dom';
import Popup from "reactjs-popup";

import {
  Card,
  CardColumns,
  CardBody,
  CardHeader,
} from 'reactstrap';


class PersonalityInsights extends Component {
  constructor(props) {
    super(props);
    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
    this.openPopup = this.openPopup.bind(this);
    this.closePopup = this.closePopup.bind(this);
    this.state = {
      filtercod:'%',
      filtercpf:'',
      filteremail:'',
      usersData: [],
      popup: {
        open : false,
        message : ''
      },
    };
  }

  componentDidMount(){
    this.performSearch();
    this.setState({
      filtercod: ''
    });
  }

  clearFields = () => {
    this.setState({
      filtercod:'',
      filtercpf:'',
      filteremail:'',
      sqlquery:'',
      response:'',
      post:'',
      responseToPost:''
    });
  }

  performSearch = () => {
      fetch('api/selectCandidato?table=CANDIDATO&cod='+this.state.filtercod +'&cpf='+this.state.filtercpf+'&email='+this.state.filteremail)
      .then(res => res.json())
      .then(res => {
        var aux = [];
        res.recordset.forEach(e => {
          aux.push(e);
        });
        this.setState({
          usersData : aux
        })
        this.renderTableData();

      })
      .catch(err => {
        this.openPopup("Erro ao filtrar candidatos. " + err);
      });
  }

  renderTableData(){
    return this.state.usersData.map((usersData, index) => {
      const {cod_candidato, nome, sobrenome, email, endereco } = usersData;
      const userLink = `/perfil/${cod_candidato}`
      return (
        <tr key={cod_candidato}>
          <th scope="row"><Link to={userLink}>{cod_candidato}</Link></th>
          <td><Link to={userLink}>{nome}&nbsp;{sobrenome}</Link></td>
          <td>{email}</td>
          <td><Link to={userLink}></Link></td>
        </tr>
      )
    });
    }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  onRadioBtnClick(radioSelected) {
    this.setState({
      sex: radioSelected.target.value,
    }); 
  }

  openPopup(message){
    this.setState({
      popup : {
        open : true,
        message : message
      }
    });
  }

  closePopup(){
    this.setState({
      popup: {
        open : false,
        message: ''
      }
    });
  }

  render() {
    return(

      <div className="animated fadeIn">
        <CardColumns className="cols-2">
          <Card>
            <CardHeader>
              Line Chart
              <div className="card-header-actions">
                <a href="http://www.chartjs.org" className="card-header-action">
                  <small className="text-muted">docs</small>
                </a>
              </div>
            </CardHeader>
            <CardBody>
              <div className="chart-wrapper">
                <Line data={line} options={options} />
              </div>
            </CardBody>
          </Card>
          <Card>
            <CardHeader>
              Bar Chart
              <div className="card-header-actions">
                <a href="http://www.chartjs.org" className="card-header-action">
                  <small className="text-muted">docs</small>
                </a>
              </div>
            </CardHeader>
            <CardBody>
              <div className="chart-wrapper">
                <Bar data={bar} options={options} />
              </div>
            </CardBody>
          </Card>
          <Card>
            <CardHeader>
              Doughnut Chart
              <div className="card-header-actions">
                <a href="http://www.chartjs.org" className="card-header-action">
                  <small className="text-muted">docs</small>
                </a>
              </div>
            </CardHeader>
            <CardBody>
              <div className="chart-wrapper">
                <Doughnut data={doughnut} />
              </div>
            </CardBody>
          </Card>
          <Card>
            <CardHeader>
              Radar Chart
              <div className="card-header-actions">
                <a href="http://www.chartjs.org" className="card-header-action">
                  <small className="text-muted">docs</small>
                </a>
              </div>
            </CardHeader>
            <CardBody>
              <div className="chart-wrapper">
                <Radar data={radar} />
              </div>
            </CardBody>
          </Card>
          <Card>
            <CardHeader>
              Pie Chart
              <div className="card-header-actions">
                <a href="http://www.chartjs.org" className="card-header-action">
                  <small className="text-muted">docs</small>
                </a>
              </div>
            </CardHeader>
            <CardBody>
              <div className="chart-wrapper">
                <Pie data={pie} />
              </div>
            </CardBody>
          </Card>
          <Card>
            <CardHeader>
              Polar Area Chart
              <div className="card-header-actions">
                <a href="http://www.chartjs.org" className="card-header-action">
                  <small className="text-muted">docs</small>
                </a>
              </div>
            </CardHeader>
            <CardBody>
              <div className="chart-wrapper">
                <Polar data={polar} options={options}/>
              </div>
            </CardBody>
          </Card>
        </CardColumns>
      </div>
    )
  }
}
export default PersonalityInsights;
